import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import { PanelHeader, FixedLayout, Tabs, TabsItem } from '@vkontakte/vkui';
import { TAB_EVENTS, TAB_WORK, TAB_MAP, TAB_ACCOUNT, PANEL_MAIN } from '../constants';
import TabEvents from './TabEvents';
import TabAccount from './TabAccount';
import TabMap from './TabMap';
import TabWork from './TabWork';

export default class Main extends Component {
  static propTypes = {
    activeTab: PropTypes.string,
    go: PropTypes.func.isRequired,
    update: PropTypes.func.isRequired,
  };

  static defaultProps = {
    activeTab: TAB_EVENTS,
  };

  onTabClick = (e) => {
    this.props.update(PANEL_MAIN, {
      activeTab: e.currentTarget.dataset.id,
    });
  };

  getTabComponent = () => {
    const { activeTab } = this.props;

    switch (activeTab) {
      case TAB_EVENTS:
        return TabEvents;

      case TAB_WORK:
        return TabWork;

      case TAB_MAP:
        return TabMap;

      case TAB_ACCOUNT:
        return TabAccount;

      default:
        return <div>Что-то пошло не так, нет такой вкладки</div>;
    }
  }

  render() {
    const { activeTab } = this.props;
    const TabComponent = this.getTabComponent();

    return (
      <Fragment>
        <PanelHeader>ПОЛИТЕХ</PanelHeader>
        <FormLayout>
        <Select top="Пол" placeholder="Выберите пол">
              <option value="m">Мужской</option>
              <option value="f">Женский</option>
            </Select>
        </FormLayout>    
        <TabComponent
          go={this.props.go}
          update={this.props.update}
        />

        <FixedLayout vertical="bottom">
          <Tabs>
            <TabsItem
              data-id={TAB_EVENTS}
              selected={activeTab === TAB_EVENTS}
              onClick={this.onTabClick}
            >Мероприятия</TabsItem>

            <TabsItem
              data-id={TAB_WORK}
              selected={activeTab === TAB_WORK}
              onClick={this.onTabClick}
            >Работа</TabsItem>

            <TabsItem
              data-id={TAB_MAP}
              selected={activeTab === TAB_MAP}
              onClick={this.onTabClick}
            >Карта</TabsItem>

            <TabsItem
              data-id={TAB_ACCOUNT}
              selected={activeTab === TAB_ACCOUNT}
              onClick={this.onTabClick}
            >Аккаунт</TabsItem>
          </Tabs>
        </FixedLayout>
      </Fragment>
    );
  }
}
